mapper.select:
SELECT
SUM(IF(c.plan_type='R',0,b.pay_app_amount)) AS gtotalappamount, SUM(IF(c.plan_type='R',0,b.pay_discount)) AS gtotaldiscount, SUM(IF(c.plan_type='R',0,b.pay_ack_amount)) AS gtotalackamount, SUM(IF(c.plan_type='R',0,1)) AS gallnum, SUM(IF(c.plan_type='R',0, (IF(IFNULL(b.is_pay ,'F')='Y',1,0)))) AS gyespaynum, SUM(IF(c.plan_type='R',0, (IF(IFNULL(b.is_pay ,'F')='Y',0,1)))) AS gnopaynum, SUM(IF(c.plan_type='R',0, (IF(IFNULL(b.is_pay ,'F')='Y',b.pay_ack_amount,0)))) AS gyespayamount, SUM(IF(c.plan_type='R',0, (IF(IFNULL(b.is_pay ,'F')='Y',0,b.pay_ack_amount)))) AS gnopayamount, SUM(IF(c.plan_type='R',b.pay_app_amount,0)) AS rtotalappamount, SUM(IF(c.plan_type='R',b.pay_discount,0)) AS rtotaldiscount, SUM(IF(c.plan_type='R',b.pay_ack_amount,0)) AS rtotalackamount, SUM(IF(c.plan_type='R',1,0)) AS rallnum, SUM(IF(c.plan_type='R', (IF(IFNULL(b.is_pay ,'F')='Y',1,0)),0)) AS ryespaynum, SUM(IF(c.plan_type='R', (IF(IFNULL(b.is_pay ,'F')='Y',0,1)),0)) AS rnopaynum, SUM(IF(c.plan_type='R', (IF(IFNULL(b.is_pay ,'F')='Y',b.pay_ack_amount,0)),0)) AS ryespayamount, SUM(IF(c.plan_type='R', (IF(IFNULL(b.is_pay ,'F')='Y',0,b.pay_ack_amount)),0)) AS rnopayamount FROM org_plan_datail b, org_plan c where c.plan_id = b.plan_id and b.org_id = c.org_id and c.stats <> 'C' and b.org_id = #{orgid,jdbcType=VARCHAR}
and c.grade_id = #{parameter,jdbcType=VARCHAR}
and c.term_id = #{parameter,jdbcType=VARCHAR}
and left(c.paydate,6) = #{parameter,jdbcType=VARCHAR}
GROUP BY b.org_id

SELECT a.org_id as orgid, c.invnm as orgnm, b.name as studentnm, a.student_id as studentid, a.detail_id as detailid, a.plan_id as planid, a.pay_app_amount as payappamount, a.pay_discount as paydiscount, a.pay_ack_amount as payackamount, a.is_pay as ispay, a.pay_custno as paycustno, date_format(a.pay_date,'%Y-%m-%d %H:%i:%s') as paydate, date_format(a.plan_date,'%Y-%m-%d') as plandate FROM student b, org_plan_datail a, custinfo c where a.student_id =b.sid and a.org_id = c.custno and date_format(a.plan_date,'%Y-%m') = date_format(sysdate(),'%Y-%m')
and a.org_id = #{orgid,jdbcType=VARCHAR}
and b.p_custno = #{custno,jdbcType=VARCHAR} order by a.student_id, a.plan_date desc
