mapper.select:
 	select 
	 	id        ,
		orgid     ,
		year      ,
		type      ,
		start     ,
		end       ,
		date_format(updatetime,'%Y-%m-%d') updatetime
   from org_term where orgid=#{orgid,jdbcType=VARCHAR}
   
  		and id=#{id,jdbcType=VARCHAR}

mapper.update:
 	update org_term set 
	 	start=#{start     ,jdbcType=VARCHAR},
		end=#{end       ,jdbcType=VARCHAR},
		updatetime=now()
	where id=#{id,jdbcType=VARCHAR}
