mapper.select:
		SELECT
			count(t.tradeacco)
		FROM
			tradeaccoinfo t, bankcardinfo b
		WHERE 
			t.BANKSERIALID = b.SERIALID
			AND t.TRADEACCOST <> 'F'
  		
  			AND t.FUNDCORPNO = #{fundcorpno,jdbcType=VARCHAR}
  		
  		
  		 	AND b.BANKNO = #{bankno,jdbcType=VARCHAR}
  		
  		
  		 	AND b.bankacco = #{bankacco,jdbcType=VARCHAR}

select t.TRADEACCO, t.FUNDCORPNO, t.TRADEACCOST, t.LEVEL, b.SERIALID, b.CUSTNO, b.BANKNO, b.BANKACCO, b.CERTTYPE, b.CERTNO, b.STATE, b.PROVINCE, b.CITY, b.SUBBANK, b.UPDATETIME, c.level as clevel from tradeaccoinfo t, bankcardinfo b,bankbase c where b.serialid = t.bankserialid and b.bankno=c.bankno and t.tradeaccost = 'N'
and t.fundcorpno = #{fundcorpno,jdbcType=VARCHAR}
and b.custno = #{custno,jdbcType=VARCHAR}
